// Extract data from webhook
const optimizedResume = $input.first().json.body.optimizedResume || "";
const originalResume = $input.first().json.body.originalResume || "";
const jobDescription = $input.first().json.body.jobDescription || "";
const companyName = $input.first().json.body.companyName || "Hiring Team";
const roleName = $input.first().json.body.roleName || "this position";
const matchingSkills = $input.first().json.body.matchingSkills || [];

// Extract key requirements from job description (first 3-4 main points)
const reqSection = jobDescription.match(/qualifications|requirements|must have|you have|ideal candidate/i);
const keyRequirements = reqSection ? 
  jobDescription.substring(jobDescription.indexOf(reqSection[0]), jobDescription.indexOf(reqSection[0]) + 500) : 
  jobDescription.substring(0, 500);

// Prepare the prompt for cover letter and referral email
return {
  prompt: `You are a professional resume writer creating application documents. Generate a cover letter and referral email based on this information:

Candidate's Optimized Resume:
${optimizedResume}

Job Description:
${jobDescription.substring(0, 2500)}

Company: ${companyName}
Role: ${roleName}

Key Matching Skills:
${matchingSkills.map(s => s.skill).join(', ')}

IMPORTANT FORMATTING RULES:

For the COVER LETTER:
- Address it to "${companyName} Hiring Team" or "Dear ${companyName} Team"
- Start with: "I'm writing to apply for the ${roleName} position at ${companyName}."
- Structure: 
  1. Opening paragraph: Express interest and briefly state years of experience and main qualification
  2. "Why I'm a strong fit:" section with 2-3 specific paragraphs, each focusing on a key requirement
  3. Use specific examples and metrics from the resume
  4. Include a section about collaboration/partnership if relevant to the role
  5. Close with enthusiasm about the company's mission and impact
- Keep professional but conversational tone
- Maximum 400 words
- Include line breaks between paragraphs

For the REFERRAL EMAIL:
- Subject line: "Referral for ${roleName} - [Candidate Name]"
- Address as "Dear Hiring Manager,"
- Structure:
  1. Brief intro: "I am writing to introduce and refer [Candidate Name] for the ${roleName} position"
  2. "About [Candidate]:" section with brief overview
  3. 3-4 key qualification highlights with specific metrics
  4. "Why ${companyName}?" section explaining fit
  5. Next steps with contact information
- Professional, third-person perspective
- Maximum 350 words
- Include placeholder [Candidate Name] and [Your Name] for personalization

Return ONLY valid JSON (no markdown, no backticks):
{
  "coverLetter": "<Full cover letter text with proper line breaks using \\n\\n between paragraphs>",
  "referralEmail": "<Full referral email text with proper line breaks using \\n\\n between sections>"
}`
};

// Extract and parse response from Gemini
const response = $input.first().json;
let rawText = '';

// Extract text from response
try {
  if (response.candidates && response.candidates[0]) {
    const candidate = response.candidates[0];
    if (candidate.content && candidate.content.parts && candidate.content.parts[0]) {
      rawText = candidate.content.parts[0].text;
    }
  }
} catch (e) {
  return {
    success: false,
    error: 'Invalid response structure from Gemini',
    timestamp: new Date().toISOString()
  };
}

if (!rawText) {
  return {
    success: false,
    error: 'No text content in response',
    timestamp: new Date().toISOString()
  };
}

// Clean the text
let cleanedText = rawText.trim();

// Remove markdown code blocks
cleanedText = cleanedText.replace(/```json\s*/gi, '');
cleanedText = cleanedText.replace(/```\s*/gi, '');

// Find JSON boundaries
const jsonStart = cleanedText.indexOf('{');
const jsonEnd = cleanedText.lastIndexOf('}');

if (jsonStart === -1 || jsonEnd === -1) {
  return {
    success: false,
    error: 'No JSON found in response',
    rawResponse: rawText,
    timestamp: new Date().toISOString()
  };
}

// Extract JSON string
let jsonString = cleanedText.substring(jsonStart, jsonEnd + 1);

// Fix escaped newlines to be proper JSON
jsonString = jsonString.replace(/\\n/g, '\n');

// Try to parse
let documents;
try {
  documents = JSON.parse(jsonString);
} catch (parseError) {
  // Try to extract fields manually if parse fails
  try {
    const coverLetterMatch = jsonString.match(/"coverLetter"\s*:\s*"([^"]+(?:\\"[^"]+)*)"/s);
    const referralEmailMatch = jsonString.match(/"referralEmail"\s*:\s*"([^"]+(?:\\"[^"]+)*)"/s);

    documents = {
      coverLetter: coverLetterMatch ? coverLetterMatch[1].replace(/\\n/g, '\n').replace(/\\"/g, '"') : '',
      referralEmail: referralEmailMatch ? referralEmailMatch[1].replace(/\\n/g, '\n').replace(/\\"/g, '"') : ''
    };
  } catch (e) {
    return {
      success: false,
      error: 'Failed to parse documents: ' + parseError.message,
      rawResponse: rawText,
      timestamp: new Date().toISOString()
    };
  }
}

// Ensure both documents exist
if (!documents.coverLetter || !documents.referralEmail) {
  return {
    success: false,
    error: 'Missing cover letter or referral email',
    documents: documents,
    timestamp: new Date().toISOString()
  };
}

// Return successful response
return {
  success: true,
  documents: {
    coverLetter: documents.coverLetter,
    referralEmail: documents.referralEmail
  },
  timestamp: new Date().toISOString()
};
